#[derive(Debug, Clone)]
pub enum Token {
    // Single-character tokens
    LParen,
    RParen,
    LBracket,
    RBracket,
    LBrace,
    RBrace, // Added LBrace, RBrace
    Comma,
    Colon,
    Minus,
    Plus,
    Star,
    Slash,
    Assign,
    Newline,

    // One or two character tokens
    Eq,
    NotEq,
    Lt,
    Gt,

    // Literals
    Identifier(String),
    String(String),
    Integer(i64),
    // FStringContent is a specialized token carrying the internal token stream
    // generated by the lexer when it encounters an f-string
    FStringContent(Vec<Token>),

    // Keywords
    Def,
    If,
    Elif,
    Else,
    Return,
    For,
    In,
    True,
    False,
    None,

    // Structural
    Indent,
    Dedent,
    Eof,
}

// Helper to determine if a token is a keyword based on its string value
impl Token {
    pub fn from_keyword(s: &str) -> Option<Token> {
        match s {
            "def" => Some(Token::Def),
            "if" => Some(Token::If),
            "elif" => Some(Token::Elif),
            "else" => Some(Token::Else),
            "return" => Some(Token::Return),
            "for" => Some(Token::For),
            "in" => Some(Token::In),
            "True" => Some(Token::True),
            "False" => Some(Token::False),
            "None" => Some(Token::None),
            _ => None,
        }
    }
}
